import React, {useState, useEffect} from 'react';
import '../styles/Style.css';
import lion from'../Images/lion.jpeg';
import safari from '../Images/safari.jpeg'
const Gallery = () => {
    const [categories, setCategories] = useState([]);  
    const [categoryName, setCategoryName] = useState('');
    const [showGallery, setShowGallery] = useState(true);
    const [successMessage, setSuccessMessage] = useState('');

    //fetch categories
    useEffect(() => {
      const fetchCategories = async () => {
        try {
          const response = await fetch('http://localhost:5000/api/categories');
          if (!response.ok) {
            throw new Error('Failed to fetch categories');
          }
          const data = await response.json();
          setCategories(data);

        } catch (error) {
          console.error('Error fetching categories:', error);
        }
      };

      fetchCategories();
    },[]);

    const toggleGallery = () => setShowGallery(!showGallery);

    const addCategory = async () => {
      if (categoryName.trim()) {
        try {

          const response = await fetch('htpp://localhost:5000/api/categories', {
            method: 'POST',
           headers: { 'Content-Type': 'application/json'},
           body: JSON.stringify({ name: categoryName}),
          });

          if (!response.ok) {
            const errorDetails = await response.json();
            console.error('Server Error:', errorDetails);
            alert('Failed to add category:' + errorDetails.message);
            return;
          }

          const newCategory = await response.json();
          setCategories([...categories, newCategory]);
          setCategoryName('');
          setSuccessMessage('Category added successfully!');
          console.log('Category saved:', newCategory);
        } catch (error) {

          console.error('Error saving category:', error);
        }
      } else {
        alert
      }
    }
    return (
        <div>
          <h4>Tourist Site in Kenya</h4>
          <div>
            {Object.keys(categories).map((category) => (
              <div key={category} className="category">
                <h2>{category}</h2>
                <div className="parks">
                  {categories[category].map((park) => (
                    <div key={park.name} className="park-card">
                      <img src={park.imageUrl} alt={park.name} className="park-image" />

                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </div>
      );
    };


export default Gallery
