import React from 'react';
import axios from 'axios';


const ChatBot = () => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');

    const sendMessage = async () => {
    const response = await axios.post('http://localhost:5000/api/chatbot', {message: input});
    setMessage([...messages, {text: input, user: 'user'}, {text: response.data.reply, user: 'bot'}]);
    setInput('');
    };

    return (
        <div>
            <h2>Chat with our AI Assistant</h2>
            <div>
                {messages.map((msg, index) => (
                    <div key={index} className={msg.user === 'user' ? 'user-msg' : 'bot-msg'}>
                        {msg.txt}
                        </div>
                ))}
            </div>
            <input
                 type="text"
                 value={input}
                 onChange={(e) => setInput(e.target.value)}
                 placeholder="Ask me everything...."
                 />
                 <button onClick={sendMessage}>Send</button>
        </div>
    );
};

export default ChatBot;
