import React, {useState, useEffect} from 'react';
import '../styles/Style.css';

const Gallery = () => {
    const [categories, setCategories] = useState([]);  
    const [categoryName, setCategoryName] = useState('');
    const [showGallery, setShowGallery] = useState(true);
    const [successMessage, setSuccessMessage] = useState('');

    //fetch categories
    useEffect(() => {
      const fetchCategories = async () => {
        try {
          const response = await fetch('http://localhost:5000/api/categories');
          if (!response.ok) {
            throw new Error('Failed to fetch categories');
          }
          const data = await response.json();
          setCategories(data);

        } catch (error) {
          console.error('Error fetching categories:', error);
        }
      };

      fetchCategories();
    },[]);



    const addCategory = async () => {
      if (categoryName.trim()) {
        try {

          const response = await fetch('htpp://localhost:5000/api/categories', {
            method: 'POST',
           headers: { 'Content-Type': 'application/json'},
           body: JSON.stringify({ name: categoryName}),
          });

          if (!response.ok) {
            const errorDetails = await response.json();
            console.error('Server Error:', errorDetails);
            alert('Failed to add category:' + errorDetails.message);
            return;
          }

          const newCategory = await response.json();
          setCategories([...categories, newCategory]);
          setCategoryName('');
          setSuccessMessage('Category added successfully!');
          console.log('Category saved:', newCategory);
        } catch (error) {

          console.error('Error saving category:', error);
        }
      } else {
        alert('Please enter a category name');
      }
    };
    return (
        <div>
          <h4>Tourist Site in Kenya</h4>
          <div>
          {showGallery && (
          <div className="section">
            <h2>Gallery</h2>

            {/* Success Message */}
            {successMessage && <div className="success-message">{successMessage}</div>}

            <h3>Add New Category</h3>
            <div className="form-group">
              <label htmlFor="category-name">Category Name</label>
              <input
                type="text"
                id="category-name"
                value={categoryName}
                onChange={(e) => setCategoryName(e.target.value)}
                placeholder="Enter category name"
              />
            </div>
            <button className="btn" onClick={addCategory}>Add Category</button>

            <div className="categories">
              {categories.map((category) => (
                <div key={category._id} className="category">
                  <h3>{category.name}</h3>
                  <div className="parks">
                    {category.parks.map((park) => (
                      <div key={park._id} className="park">
                        <h4>{park.name}</h4>
                        <p>{park.description}</p>
                        <img src={park.image} alt={park.name} />
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
      );
    };


export default Gallery
