import React, { useState } from 'react';
import axios from 'axios';
import '../styles/Style.css'; 

const ChatBot = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [messages, setMessages] = useState([]);
    const [userInput, setUserInput] = useState('');

    // Toggle chatbot open/close
    const toggleChat = () => {
        setIsOpen(!isOpen);
    };

    // Handle user input
    const handleInputChange = (e) => {
        setUserInput(e.target.value);
    };

    // Send message to OpenAI and get response
    const handleSendMessage = async () => {
        if (!userInput) return;

        const userMessage = { sender: 'user', text: userInput };
        setMessages([...messages, userMessage]);

        try {
            const response = await axios.post('https://api.openai.com/v1/chat/completions', 
            {
                model: "gpt-3.5-turbo",
                messages: [{ role: 'user', content: userInput }]
            }, 
            {
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer YOUR_OPENAI_API_KEY_HERE`
                }
            });

            const botMessage = { sender: 'bot', text: response.data.choices[0].message.content };
            setMessages((prevMessages) => [...prevMessages, botMessage]);
        } catch (error) {
            console.error('Error communicating with OpenAI:', error);
            const errorMessage = { sender: 'bot', text: 'Something went wrong. Please try again later.' };
            setMessages((prevMessages) => [...prevMessages, errorMessage]);
        }

        setUserInput('');
    };

    return (
        <div className="chatbot-container">
            {/* Chatbot icon */}
            <div className="chatbot-icon" onClick={toggleChat}>
                ðŸ’¬
            </div>

            {isOpen && (
                <div className="chat-window">
                    <div className="chat-header">

                        <but onClick={toggleChat}>X</but>
                    </div>

                    <div className="chat-body">
                        {messages.map((message, index) => (
                            <div 
                                key={index} 
                                className={`chat-message ${message.sender === 'user' ? 'user-message' : 'bot-message'}`}
                            >
                                {message.text}
                            </div>
                        ))}
                    </div>

                    <div className="chat-footer">
                        <input 
                            type="text" 
                            placeholder="Type your message..." 
                            value={userInput} 
                            onChange={handleInputChange} 
                            onKeyDown={(e) => e.key === 'Enter' && handleSendMessage()}
                        />

                    </div>
                    <div className="butt">
                    <butt onClick={handleSendMessage}>Send</butt></div>
                </div>
            )}
        </div>
    );
};

export default ChatBot;
